import React, { useState, useEffect, useCallback } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import Modal from 'react-modal';
import DatePicker from "react-datepicker";
import { format } from 'date-fns';
import "react-datepicker/dist/react-datepicker.css";
import './HealthProfilePage.css';

Modal.setAppElement('#root');

const HealthProfilePage = () => {
    const history = useHistory();
    const { childId } = useParams();
    const [child, setChild] = useState(null);
    const [visits, setVisits] = useState([]);
    const [isVisitModalOpen, setIsVisitModalOpen] = useState(false);
    const [newVisit, setNewVisit] = useState({ date: new Date(), doctorName: '', reason: '', summary: '' });

    const fetchChildData = useCallback(async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/children/${childId}`);
            if (!response.ok) throw new Error('Child not found');
            const data = await response.json();
            setChild(data);
        } catch (error) { history.push('/my-children'); }
    }, [childId, history]);

    const fetchVisits = useCallback(async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/visits/${childId}`);
            const data = await response.json();
            setVisits(data);
        } catch (error) { console.error("Failed to fetch visits", error); }
    }, [childId]);

    useEffect(() => {
        fetchChildData();
        fetchVisits();
    }, [fetchChildData, fetchVisits]);

    const handleVisitChange = (e) => setNewVisit(prev => ({ ...prev, [e.target.name]: e.target.value }));
    const handleVisitDateChange = (date) => setNewVisit(prev => ({ ...prev, date }));

    const handleVisitSubmit = async (e) => {
        e.preventDefault();
        const visitData = { ...newVisit, date: format(newVisit.date, 'yyyy/MM/dd') };
        try {
            const response = await fetch(`http://localhost:5000/api/visits/${childId}`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(visitData) });
            if (!response.ok) throw new Error('Failed to add visit');
            fetchVisits();
            setIsVisitModalOpen(false);
            setNewVisit({ date: new Date(), doctorName: '', reason: '', summary: '' });
        } catch (error) { alert(error.message); }
    };

    if (!child) return <p>در حال بارگذاری اطلاعات...</p>;

    const avatarUrl = child.avatar && child.avatar.startsWith('/uploads') ? `http://localhost:5000${child.avatar}` : (child.avatar || 'https://i.pravatar.cc/100');

    return (
        <div className="health-profile-page">
            <nav className="page-nav-final"><button onClick={() => history.push('/my-children')} className="back-btn-profile">&larr; لیست کودکان</button><h1>پرونده سلامت</h1><div className="nav-placeholder"></div></nav>
            <header className="profile-header"><img src={avatarUrl} alt={child.name} className="profile-avatar" /><div className="profile-header-info"><h2>{child.name}</h2><p>{child.age} - ({child.gender === 'boy' ? 'پسر' : 'دختر'})</p></div></header>
            <main className="profile-content">
                <div className="profile-grid">
                    <div className="profile-card main-info">
                        <h3>اطلاعات پایه</h3>
                        <div className="info-grid"><div className="info-item"><strong>گروه خونی:</strong><span>{child.bloodType || 'ثبت نشده'}</span></div><div className="info-item"><strong>جنسیت:</strong><span>{child.gender === 'boy' ? 'پسر' : 'دختر'}</span></div><div className="info-item full-width"><strong>آلرژی‌ها:</strong><span>{child.allergies || 'آلرژی خاصی ثبت نشده است.'}</span></div><div className="info-item full-width"><strong>بیماری‌های خاص:</strong><span>{child.special_illnesses || 'بیماری خاصی ثبت نشده است.'}</span></div></div>
                        <button onClick={() => history.push(`/edit-child/${child.id}`)} className="edit-info-btn">ویرایش اطلاعات پایه</button>
                    </div>
                    <div className="profile-card visits-history">
                        <h3>تاریخچه مراجعات پزشکی</h3>
                        <button onClick={() => setIsVisitModalOpen(true)} className="add-visit-btn">+ افزودن مراجعه</button>
                        <div className="visits-list">{visits.length === 0 ? <p>مراجعه‌ای ثبت نشده.</p> : visits.map((visit, index) => (<div key={index} className="visit-item"><strong>{visit.date} - {visit.doctorName}</strong><p>دلیل: {visit.reason}</p>{visit.summary && <small>خلاصه: {visit.summary}</small>}</div>)) }</div>
                    </div>
                    <div className="profile-card full-width"><button onClick={() => history.push(`/growth-chart/${childId}`)} style={{width: '100%', padding: '1rem', fontSize: '1.1rem', fontWeight: 'bold'}}>مشاهده و مدیریت نمودار رشد</button></div>
                </div>
            </main>
            <Modal isOpen={isVisitModalOpen} onRequestClose={() => setIsVisitModalOpen(false)} className="modal" overlayClassName="overlay"><div className="modal-content"><h2>ثبت مراجعه جدید</h2><form onSubmit={handleVisitSubmit}><div className="form-group"><label>تاریخ مراجعه</label><DatePicker selected={newVisit.date} onChange={handleVisitDateChange} dateFormat="yyyy/MM/dd" /></div><div className="form-group"><label>نام پزشک</label><input name="doctorName" onChange={handleVisitChange} required /></div><div className="form-group"><label>دلیل مراجعه</label><input name="reason" onChange={handleVisitChange} required /></div><div className="form-group"><label>خلاصه تشخیص/داروها</label><textarea name="summary" rows="4" onChange={handleVisitChange}></textarea></div><div className="modal-actions"><button type="submit" className="btn-save">ثبت</button><button type="button" onClick={() => setIsVisitModalOpen(false)} className="btn-cancel">انصراف</button></div></form></div></Modal>
        </div>
    );
};

export default HealthProfilePage;
