import React, { useState, useEffect, useCallback } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import DatePicker from "react-datepicker";
import { format, differenceInMonths, parseISO } from 'date-fns';
import Modal from 'react-modal';
import "react-datepicker/dist/react-datepicker.css";
import './GrowthChartPage.css';
import { heightForAgeBoys, weightForAgeBoys } from '../who-stats';

Modal.setAppElement('#root');

const mergeData = (childBirthDate, growthData, whoData, dataKey) => {
    if (!childBirthDate) return [];
    const combined = whoData.map(who => ({ ...who }));
    growthData.forEach(record => {
        const recordDate = parseISO(record.date.replace(/\//g, '-'));
        const birthDate = parseISO(childBirthDate.replace(/\//g, '-'));
        const ageInMonths = differenceInMonths(recordDate, birthDate);
        const existing = combined.find(c => c.month === ageInMonths);
        if (existing) { existing[dataKey] = record[dataKey]; }
        else { combined.push({ month: ageInMonths, [dataKey]: record[dataKey] }); }
    });
    return combined.sort((a, b) => a.month - b.month);
};

const GrowthChartPage = () => {
    const history = useHistory();
    const { childId } = useParams();
    const [child, setChild] = useState(null);
    const [growthData, setGrowthData] = useState([]);
    const [newRecord, setNewRecord] = useState({ date: new Date(), height: '', weight: '' });
    const [isModalOpen, setIsModalOpen] = useState(false);

    const fetchAllData = useCallback(async () => { /* ... */ }, [childId, history]);
    useEffect(() => { fetchAllData(); }, [fetchAllData]);

    const handleFormSubmit = async (e) => { /* ... */ };

    if (!child) return <p>در حال بارگذاری...</p>;
    
    const mergedHeightData = mergeData(child.birthDate, growthData, heightForAgeBoys, 'height');
    const mergedWeightData = mergeData(child.birthDate, growthData, weightForAgeBoys, 'weight');

    return (
        <div className="growth-chart-page">
            <nav className="page-nav-final"><button onClick={() => history.push('/my-children')}>&larr; بازگشت</button><h1>پرونده رشد: {child.name}</h1><div className="nav-placeholder"></div></nav>
            <div className="growth-content-container">
                <button onClick={() => setIsModalOpen(true)}>+ ثبت رکورد جدید</button>
                <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)}>{/* ... form ... */}</Modal>
                <div className="chart-card"><h2>نمودار قد</h2><ResponsiveContainer width="100%" height={400}><LineChart data={mergedHeightData}><CartesianGrid /><XAxis dataKey="month" /><YAxis /><Tooltip /><Legend /><Line dataKey="height" name="قد کودک" stroke="#8884d8" /><Line dataKey="P50" name="میانه جهانی" stroke="#4caf50" /><Line dataKey="P3" name="صدک ۳" stroke="#ff7300" dot={false} /><Line dataKey="P97" name="صدک ۹۷" stroke="#ff7300" dot={false} /></LineChart></ResponsiveContainer></div>
                <div className="chart-card"><h2>نمودار وزن</h2><ResponsiveContainer width="100%" height={400}><LineChart data={mergedWeightData}><CartesianGrid /><XAxis dataKey="month" /><YAxis /><Tooltip /><Legend /><Line dataKey="weight" name="وزن کودک" stroke="#82ca9d" /><Line dataKey="P50" name="میانه جهانی" stroke="#4caf50" /><Line dataKey="P3" name="صدک ۳" stroke="#ff7300" dot={false} /><Line dataKey="P97" name="صدک ۹۷" stroke="#ff7300" dot={false} /></LineChart></ResponsiveContainer></div>
            </div>
        </div>
    );
};

export default GrowthChartPage;
