import React, { useState, useEffect, useCallback } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import DatePicker from "react-datepicker";
import { format, differenceInMonths, parseISO } from 'date-fns';
import Modal from 'react-modal';
import "react-datepicker/dist/react-datepicker.css";
import './GrowthChartPage.css';
import { heightForAgeBoys, weightForAgeBoys, heightForAgeGirls, weightForAgeGirls } from '../who-stats';

Modal.setAppElement('#root');

const GrowthChartPage = () => {
    const history = useHistory();
    const { childId } = useParams();
    const [child, setChild] = useState(null);
    const [growthData, setGrowthData] = useState([]);
    // ... (other states and handlers remain the same)

    const fetchAllData = useCallback(async () => { /* ... */ }, [childId, history]);
    useEffect(() => { fetchAllData(); }, [fetchAllData]);
    const handleFormSubmit = async (e) => { /* ... */ };

    if (!child) return <p>در حال بارگذاری...</p>;

    // --- NEW: Process user growth data separately ---
    const processedGrowthData = growthData.map(record => {
        try {
            const ageInMonths = differenceInMonths(parseISO(record.date.replace(/\//g, '-')), parseISO(child.birthDate.replace(/\//g, '-')));
            return { month: ageInMonths, height: record.height, weight: record.weight };
        } catch { return null; }
    }).filter(Boolean).sort((a, b) => a.month - b.month);

    const heightStandard = child.gender === 'girl' ? heightForAgeGirls : heightForAgeBoys;
    const weightStandard = child.gender === 'girl' ? weightForAgeGirls : weightForAgeBoys;
    const genderText = child.gender === 'girl' ? 'دختران' : 'پسران';

    return (
        <div className="growth-chart-page">
            {/* ... Nav and Modal ... */}
            <div className="growth-content-container">
                {/* ... Summary cards and history table ... */}
                <div className="chart-card"><h2>نمودار قد به سن ({genderText})</h2>
                    <ResponsiveContainer width="100%" height={400}>
                        <LineChart margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis type="number" dataKey="month" name="سن (ماه)" domain={['dataMin', 'dataMax']} allowDuplicatedCategory={false} />
                            <YAxis label={{ value: 'قد (cm)', angle: -90, position: 'insideLeft' }} />
                            <Tooltip />
                            <Legend />
                            {/* WHO Standard Lines */}
                            <Line data={heightStandard} type="monotone" dataKey="P3" stroke="#ffc658" dot={false} name="صدک ۳" />
                            <Line data={heightStandard} type="monotone" dataKey="P50" stroke="#4caf50" dot={false} name="میانه" />
                            <Line data={heightStandard} type="monotone" dataKey="P97" stroke="#ffc658" dot={false} name="صدک ۹۷" />
                            {/* Child's Growth Line */}
                            <Line data={processedGrowthData} type="monotone" dataKey="height" stroke="#8884d8" strokeWidth={3} name="قد کودک" connectNulls />
                        </LineChart>
                    </ResponsiveContainer>
                </div>
                {/* ... Similar structure for Weight Chart ... */}
            </div>
        </div>
    );
};

export default GrowthChartPage;
