import "react-datepicker/dist/react-datepicker.css";
import './GrowthChartPage.css';
import { heightForAgeBoys, weightForAgeBoys, heightForAgeGirls, weightForAgeGirls } from '../who-stats';

Modal.setAppElement('#root');

const mergeData = (childBirthDate, growthData, whoData, dataKey) => {
    if (!childBirthDate) return [];
    const combined = whoData.map(who => ({ ...who }));
    growthData.forEach(record => {
        try {
            const recordDate = parseISO(record.date.replace(/\//g, '-'));
            const birthDate = parseISO(childBirthDate.replace(/\//g, '-'));
            const ageInMonths = differenceInMonths(recordDate, birthDate);
            const existing = combined.find(c => c.month === ageInMonths);
            if (existing) { existing[dataKey] = record[dataKey]; }
            else { combined.push({ month: ageInMonths, [dataKey]: record[dataKey] }); }
        } catch (e) { console.error("Date parsing error", e); }
    });
    return combined.sort((a, b) => a.month - b.month);
};

const GrowthChartPage = () => {
    const history = useHistory();
    const { childId } = useParams();
    const [child, setChild] = useState(null);
    const [growthData, setGrowthData] = useState([]);
    const [newRecord, setNewRecord] = useState({ date: new Date(), height: '', weight: '' });
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isHistoryVisible, setIsHistoryVisible] = useState(false);

    const fetchAllData = useCallback(async () => {
        try {
            const childRes = await fetch(`http://localhost:5000/api/children/${childId}`);
            if (!childRes.ok) throw new Error('Child not found');
            const childData = await childRes.json();
            setChild(childData);
            const growthRes = await fetch(`http://localhost:5000/api/growth/${childId}`);
            const growthRecords = await growthRes.json();
            setGrowthData(growthRecords);
        } catch (error) { history.push('/my-children'); }
    }, [childId, history]);

    useEffect(() => { fetchAllData(); }, [fetchAllData]);

    const handleInputChange = (e) => setNewRecord(prev => ({ ...prev, [e.target.name]: e.target.value }));
    const handleDateChange = (date) => setNewRecord(prev => ({ ...prev, date }));

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        const formattedRecord = { ...newRecord, date: format(newRecord.date, 'yyyy/MM/dd') };
        try {
            const response = await fetch(`http://localhost:5000/api/growth/${childId}`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(formattedRecord) });
            if (!response.ok) throw new Error('Failed to add record');
            fetchAllData();
            setIsModalOpen(false);
            setNewRecord({ date: new Date(), height: '', weight: '' });
        } catch (error) { alert(error.message); }
    };

    const handleDeleteRecord = async (date) => {
        if (window.confirm(`آیا از حذف رکورد تاریخ ${date} مطمئن هستید؟`)) {
            try {
                await fetch(`http://localhost:5000/api/growth/${childId}/${encodeURIComponent(date)}`, { method: 'DELETE' });
                fetchAllData();
            } catch (error) { alert(error.message); }
        }
    };

    const latestRecord = growthData.length > 0 ? growthData[growthData.length - 1] : null;

    if (!child) return <p>در حال بارگذاری...</p>;

    const heightStandard = child.gender === 'girl' ? heightForAgeGirls : heightForAgeBoys;
    const weightStandard = child.gender === 'girl' ? weightForAgeGirls : weightForAgeBoys;
    const genderText = child.gender === 'girl' ? 'دختران' : 'پسران';

    const mergedHeightData = mergeData(child.birthDate, growthData, heightStandard, 'height');
    const mergedWeightData = mergeData(child.birthDate, growthData, weightStandard, 'weight');

    return (
        <div className="growth-chart-page">
            <nav className="page-nav-final"><button onClick={() => history.push('/my-children')} className="back-btn-growth">&larr; بازگشت</button><h1>پرونده رشد: {child.name}</h1><div className="nav-placeholder"></div></nav>
            <div className="growth-content-container">
                <button onClick={() => setIsModalOpen(true)} className="add-record-btn">+ ثبت رکورد جدید</button>
                <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)} className="modal" overlayClassName="overlay">
                    <div className="modal-content">
                        <h2>ثبت اطلاعات جدید</h2>
                        <form onSubmit={handleFormSubmit} className="growth-form-modal">
                            <div className="form-group"><label>تاریخ</label><DatePicker selected={newRecord.date} onChange={handleDateChange} dateFormat="yyyy/MM/dd" /></div>
                            <div className="form-group"><label>قد (سانتی‌متر)</label><input type="number" name="height" value={newRecord.height} onChange={handleInputChange} required /></div>
                            <div className="form-group"><label>وزن (کیلوگرم)</label><input type="number" step="0.1" name="weight" value={newRecord.weight} onChange={handleInputChange} required /></div>
                            <div className="modal-actions"><button type="submit" className="btn-save">ثبت</button><button type="button" onClick={() => setIsModalOpen(false)} className="btn-cancel">انصراف</button></div>
                        </form>
                    </div>
                </Modal>
                <div className="summary-cards"><div className="summary-card"><span>آخرین قد</span><strong>{latestRecord ? `${latestRecord.height} cm` : 'N/A'}</strong></div><div className="summary-card"><span>آخرین وزن</span><strong>{latestRecord ? `${latestRecord.weight} kg` : 'N/A'}</strong></div></div>
                <div className="data-table-card"><button onClick={() => setIsHistoryVisible(!isHistoryVisible)} className="toggle-history-btn">{isHistoryVisible ? 'مخفی کردن تاریخچه' : 'نمایش تاریخچه'}</button>{isHistoryVisible && (<table><thead><tr><th>تاریخ</th><th>قد (cm)</th><th>وزن (kg)</th><th>عملیات</th></tr></thead><tbody>{growthData.map((record, index) => (<tr key={index}><td>{record.date}</td><td>{record.height}</td><td>{record.weight}</td><td><button onClick={() => handleDeleteRecord(record.date)} className="btn-delete-record">حذف</button></td></tr>))}</tbody></table>)}</div>
                <div className="chart-card"><h2>نمودار قد به سن ({genderText})</h2><ResponsiveContainer width="100%" height={400}><LineChart data={mergedHeightData}><CartesianGrid /><XAxis dataKey="month" label={{ value: 'سن (ماه)', position: 'insideBottom', offset: -5 }}/><YAxis label={{ value: 'قد (cm)', angle: -90, position: 'insideLeft' }}/><Tooltip /><Legend /><Line dataKey="height" name="قد کودک" stroke="#8884d8" strokeWidth={2} /><Line dataKey="P50" name="میانه جهانی" stroke="#4caf50" dot={false} /><Line dataKey="P3" name="صدک ۳" stroke="#ff9800" strokeDasharray="5 5" dot={false}/><Line dataKey="P97" name="صدک ۹۷" stroke="#ff9800" strokeDasharray="5 5" dot={false}/></LineChart></ResponsiveContainer></div>
                <div className="chart-card"><h2>نمودار وزن به سن ({genderText})</h2><ResponsiveContainer width="100%" height={400}><LineChart data={mergedWeightData}><CartesianGrid /><XAxis dataKey="month" label={{ value: 'سن (ماه)', position: 'insideBottom', offset: -5 }}/><YAxis label={{ value: 'وزن (kg)', angle: -90, position: 'insideLeft' }}/><Tooltip /><Legend /><Line dataKey="weight" name="وزن کودک" stroke="#82ca9d" strokeWidth={2} /><Line dataKey="P50" name="میانه جهانی" stroke="#4caf50" dot={false} /><Line dataKey="P3" name="صدک ۳" stroke="#ff9800" strokeDasharray="5 5" dot={false}/><Line dataKey="P97" name="صدک ۹۷" stroke="#ff9800" strokeDasharray="5 5" dot={false}/></LineChart></ResponsiveContainer></div>
            </div>
        </div>
    );
};

export default GrowthChartPage;
