import React, { useState, useEffect, useCallback } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import DatePicker from "react-datepicker";
import { format } from 'date-fns';
import "react-datepicker/dist/react-datepicker.css";
import './GrowthChartPage.css';

const GrowthChartPage = () => {
    const history = useHistory();
    const { childId } = useParams();
    const [child, setChild] = useState(null);
    const [growthData, setGrowthData] = useState([]);
    const [newRecord, setNewRecord] = useState({ date: new Date(), height: '', weight: '' });

    const fetchAllData = useCallback(async () => {
        try {
            const childRes = await fetch(`http://localhost:5000/api/children/${childId}`);
            const childData = await childRes.json();
            if (!childRes.ok) throw new Error('Child not found');
            setChild(childData);

            const growthRes = await fetch(`http://localhost:5000/api/growth/${childId}`);
            const growthRecords = await growthRes.json();
            setGrowthData(growthRecords);
        } catch (error) { history.push('/my-children'); }
    }, [childId, history]);

    useEffect(() => { fetchAllData(); }, [fetchAllData]);

    const handleInputChange = (e) => setNewRecord(prev => ({ ...prev, [e.target.name]: e.target.value }));
    const handleDateChange = (date) => setNewRecord(prev => ({ ...prev, date }));

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        const formattedRecord = { ...newRecord, date: format(newRecord.date, 'yyyy/MM/dd') };
        try {
            const response = await fetch(`http://localhost:5000/api/growth/${childId}`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(formattedRecord) });
            if (!response.ok) throw new Error('Failed to add record');
            fetchAllData();
            setNewRecord({ date: new Date(), height: '', weight: '' });
        } catch (error) { alert(error.message); }
    };

    const handleDeleteRecord = async (date) => {
        if (window.confirm(`آیا از حذف رکورد تاریخ ${date} مطمئن هستید؟`)) {
            try {
                const response = await fetch(`http://localhost:5000/api/growth/${childId}/${encodeURIComponent(date)}`, { method: 'DELETE' });
                if (!response.ok) throw new Error('Failed to delete record');
                fetchAllData();
            } catch (error) { alert(error.message); }
        }
    };

    const latestRecord = growthData.length > 0 ? growthData[growthData.length - 1] : null;

    if (!child) return <p>در حال بارگذاری...</p>;

    return (
        <div className="growth-chart-page">
            <nav className="page-nav-final"><button onClick={() => history.push('/my-children')} className="back-btn-growth">&larr; بازگشت</button><h1>پرونده رشد: {child.name}</h1><div className="nav-placeholder"></div></nav>
            <div className="growth-content-container">
                <div className="summary-cards"><div className="summary-card"><span>آخرین قد</span><strong>{latestRecord ? `${latestRecord.height} cm` : 'N/A'}</strong></div><div className="summary-card"><span>آخرین وزن</span><strong>{latestRecord ? `${latestRecord.weight} kg` : 'N/A'}</strong></div></div>
                <div className="add-data-card">
                    <h2>ثبت اطلاعات جدید</h2>
                    <form onSubmit={handleFormSubmit} className="growth-form"><div className="form-group"><label>تاریخ</label><DatePicker selected={newRecord.date} onChange={handleDateChange} dateFormat="yyyy/MM/dd" /></div><div className="form-group"><label>قد (سانتی‌متر)</label><input type="number" name="height" value={newRecord.height} onChange={handleInputChange} required /></div><div className="form-group"><label>وزن (کیلوگرم)</label><input type="number" step="0.1" name="weight" value={newRecord.weight} onChange={handleInputChange} required /></div><button type="submit" className="btn-save">ثبت</button></form>
                </div>
                <div className="data-table-card"><h2>تاریخچه داده‌ها</h2><table><thead><tr><th>تاریخ</th><th>قد (cm)</th><th>وزن (kg)</th><th>عملیات</th></tr></thead><tbody>{growthData.map((record, index) => (<tr key={index}><td>{record.date}</td><td>{record.height}</td><td>{record.weight}</td><td><button onClick={() => handleDeleteRecord(record.date)} className="btn-delete-record">حذف</button></td></tr>))}</tbody></table></div>
                <div className="chart-card"><h2>نمودار رشد قد</h2><ResponsiveContainer width="100%" height={300}><LineChart data={growthData}><CartesianGrid /><XAxis dataKey="date" /><YAxis domain={['auto', 'auto']} /><Tooltip /><Legend /><Line type="monotone" dataKey="height" name="قد" stroke="#8884d8" /></LineChart></ResponsiveContainer></div>
                <div className="chart-card"><h2>نمودار رشد وزن</h2><ResponsiveContainer width="100%" height={300}><LineChart data={growthData}><CartesianGrid /><XAxis dataKey="date" /><YAxis domain={['auto', 'auto']} /><Tooltip /><Legend /><Line type="monotone" dataKey="weight" name="وزن" stroke="#82ca9d" /></LineChart></ResponsiveContainer></div>
            </div>
        </div>
    );
};

export default GrowthChartPage;
