import React, { useState, useEffect, useCallback } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import DatePicker from "react-datepicker";
import { format, differenceInMonths, parseISO } from 'date-fns';
import Modal from 'react-modal';
import "react-datepicker/dist/react-datepicker.css";
import './GrowthChartPage.css';
import { heightForAgeBoys, weightForAgeBoys } from '../who-stats';

Modal.setAppElement('#root');

const mergeData = (childBirthDate, growthData, whoData, dataKey) => { /* ... (same as before) */ };

const GrowthChartPage = () => {
    const history = useHistory();
    const { childId } = useParams();
    const [child, setChild] = useState(null);
    const [growthData, setGrowthData] = useState([]);
    const [newRecord, setNewRecord] = useState({ date: new Date(), height: '', weight: '' });
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isHistoryVisible, setIsHistoryVisible] = useState(false);

    const fetchAllData = useCallback(async () => { /* ... (same as before) */ }, [childId, history]);
    useEffect(() => { fetchAllData(); }, [fetchAllData]);

    const handleFormSubmit = async (e) => { /* ... (same as before) */ };
    const handleDeleteRecord = async (date) => { /* ... (same as before) */ };

    const latestRecord = growthData.length > 0 ? growthData[growthData.length - 1] : null;

    if (!child) return <p>در حال بارگذاری...</p>;
    
    const mergedHeightData = mergeData(child.birthDate, growthData, heightForAgeBoys, 'height');
    const mergedWeightData = mergeData(child.birthDate, growthData, weightForAgeBoys, 'weight');

    return (
        <div className="growth-chart-page">
            <nav className="page-nav-final"><button onClick={() => history.push('/my-children')} className="back-btn-growth">&larr; بازگشت</button><h1>پرونده رشد: {child.name}</h1><div className="nav-placeholder"></div></nav>
            <div className="growth-content-container">
                <button onClick={() => setIsModalOpen(true)} className="add-record-btn">+ ثبت رکورد جدید</button>
                <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)} className="modal" overlayClassName="overlay">{/* ... form in modal ... */}</Modal>
                
                <div className="summary-cards">
                    <div className="summary-card"><span>آخرین قد</span><strong>{latestRecord ? `${latestRecord.height} cm` : 'N/A'}</strong></div>
                    <div className="summary-card"><span>آخرین وزن</span><strong>{latestRecord ? `${latestRecord.weight} kg` : 'N/A'}</strong></div>
                </div>

                <div className="data-table-card">
                    <button onClick={() => setIsHistoryVisible(!isHistoryVisible)} className="toggle-history-btn">{isHistoryVisible ? 'مخفی کردن تاریخچه' : 'نمایش تاریخچه'}</button>
                    {isHistoryVisible && (<table><thead><tr><th>تاریخ</th><th>قد (cm)</th><th>وزن (kg)</th><th>عملیات</th></tr></thead><tbody>{growthData.map((record, index) => (<tr key={index}><td>{record.date}</td><td>{record.height}</td><td>{record.weight}</td><td><button onClick={() => handleDeleteRecord(record.date)} className="btn-delete-record">حذف</button></td></tr>))}</tbody></table>)}
                </div>

                <div className="chart-card"><h2>نمودار قد به سن</h2><ResponsiveContainer width="100%" height={400}><LineChart data={mergedHeightData}>{/* ... chart lines ... */}</LineChart></ResponsiveContainer></div>
                <div className="chart-card"><h2>نمودار وزن به سن</h2><ResponsiveContainer width="100%" height={400}><LineChart data={mergedWeightData}>{/* ... chart lines ... */}</LineChart></ResponsiveContainer></div>
            </div>
        </div>
    );
};

export default GrowthChartPage;
