import React, { useState, useEffect, useCallback } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import Modal from 'react-modal';
import DatePicker from "react-datepicker";
import { format } from 'date-fns';
import "react-datepicker/dist/react-datepicker.css";
import './HealthProfilePage.css';

Modal.setAppElement('#root');

const HealthProfilePage = () => {
    const history = useHistory();
    const { childId } = useParams();
    const [child, setChild] = useState(null);
    const [visits, setVisits] = useState([]);
    const [documents, setDocuments] = useState([]);
    const [isVisitModalOpen, setIsVisitModalOpen] = useState(false);
    const [isDocModalOpen, setIsDocModalOpen] = useState(false);
    const [newVisit, setNewVisit] = useState({ date: new Date(), doctorName: '', reason: '', summary: '' });
    const [newDocument, setNewDocument] = useState({ title: '', file: null });

    const fetchChildData = useCallback(async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/children/${childId}`);
            if (!response.ok) throw new Error('Child not found');
            const data = await response.json();
            setChild(data);
        } catch (error) { history.push('/my-children'); }
    }, [childId, history]);

    const fetchVisits = useCallback(async () => { /* ... */ }, [childId]);
    const fetchDocuments = useCallback(async () => { /* ... */ }, [childId]);

    useEffect(() => {
        fetchChildData();
        fetchVisits();
        fetchDocuments();
    }, [fetchChildData, fetchVisits, fetchDocuments]);

    const handleVisitSubmit = async (e) => { /* ... */ };
    const handleDocSubmit = async (e) => { /* ... */ };
    // ... (other handlers)

    if (!child) return <p>در حال بارگذاری اطلاعات...</p>;

    const avatarUrl = child.avatar && child.avatar.startsWith('/uploads') ? `http://localhost:5000${child.avatar}` : (child.avatar || 'https://i.pravatar.cc/100');

    return (
        <div className="health-profile-page">
            <nav className="page-nav-final"><button onClick={() => history.push('/my-children')} className="back-btn-profile">&larr; لیست کودکان</button><h1>پرونده سلامت</h1><div className="nav-placeholder"></div></nav>
            <header className="profile-header"><img src={avatarUrl} alt={child.name} className="profile-avatar" /><div className="profile-header-info"><h2>{child.name}</h2><p>{child.age} - ({child.gender === 'boy' ? 'پسر' : 'دختر'})</p></div></header>
            <main className="profile-content">
                <div className="profile-grid">
                    <div className="profile-card main-info"><h3>اطلاعات پایه</h3><div className="info-grid"><div className="info-item"><strong>گروه خونی:</strong><span>{child.bloodType || 'ثبت نشده'}</span></div><div className="info-item"><strong>جنسیت:</strong><span>{child.gender === 'boy' ? 'پسر' : 'دختر'}</span></div><div className="info-item full-width"><strong>آلرژی‌ها:</strong><span>{child.allergies || 'موردی ثبت نشده.'}</span></div><div className="info-item full-width"><strong>بیماری‌های خاص:</strong><span>{child.special_illnesses || 'موردی ثبت نشده.'}</span></div></div><button onClick={() => history.push(`/edit-child/${child.id}`)} className="edit-info-btn">ویرایش</button></div>
                    <div className="profile-card visits-history"><h3>تاریخچه مراجعات</h3><button onClick={() => setIsVisitModalOpen(true)} className="add-visit-btn">+ افزودن</button><div className="visits-list">{visits.length === 0 ? <p>مراجعه‌ای ثبت نشده.</p> : visits.map((v, i) => (<div key={i} className="visit-item"><strong>{v.date} - {v.doctorName}</strong><p>{v.reason}</p></div>))}</div></div>
                    <div className="profile-card documents-card"><h3>مدارک پزشکی</h3><button onClick={() => setIsDocModalOpen(true)} className="add-document-btn">+ آپلود</button><div className="documents-list">{documents.length === 0 ? <p>مدرکی آپلود نشده.</p> : documents.map((d, i) => (<div key={i} className="document-item"><a href={`http://localhost:5000${d.filePath}`} target="_blank">{d.title}</a><span>{d.uploadDate}</span></div>))}</div></div>
                    <div className="profile-card full-width"><button onClick={() => history.push(`/growth-chart/${childId}`)} style={{width:'100%'}}>مدیریت نمودار رشد</button></div>
                </div>
            </main>
            <Modal isOpen={isVisitModalOpen} onRequestClose={() => setIsVisitModalOpen(false)}>{/* ... Visit Modal ... */}</Modal>
            <Modal isOpen={isDocModalOpen} onRequestClose={() => setIsDocModalOpen(false)}>{/* ... Document Modal ... */}</Modal>
        </div>
    );
};

export default HealthProfilePage;
