import React, { useState, useEffect, useCallback } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import DatePicker from "react-datepicker";
import { format } from 'date-fns';
import Modal from 'react-modal';
import "react-datepicker/dist/react-datepicker.css";
import './GrowthChartPage.css';

// Set the app element for react-modal for accessibility
Modal.setAppElement('#root');

const GrowthChartPage = () => {
    const history = useHistory();
    const { childId } = useParams();
    const [child, setChild] = useState(null);
    const [growthData, setGrowthData] = useState([]);
    const [newRecord, setNewRecord] = useState({ date: new Date(), height: '', weight: '' });
    const [isHistoryVisible, setIsHistoryVisible] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false); // State for modal

    const fetchAllData = useCallback(async () => { /* ... (same as before) */ }, [childId, history]);
    useEffect(() => { fetchAllData(); }, [fetchAllData]);

    const handleInputChange = (e) => setNewRecord(prev => ({ ...prev, [e.target.name]: e.target.value }));
    const handleDateChange = (date) => setNewRecord(prev => ({ ...prev, date }));

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        // ... (same submit logic as before)
        setIsModalOpen(false); // Close modal on submit
    };

    const handleDeleteRecord = async (date) => { /* ... (same as before) */ };

    const latestRecord = growthData.length > 0 ? growthData[growthData.length - 1] : null;

    if (!child) return <p>در حال بارگذاری...</p>;

    return (
        <div className="growth-chart-page">
            <nav className="page-nav-final">{/* ... */}</nav>
            <div className="growth-content-container">
                <button onClick={() => setIsModalOpen(true)} className="add-record-btn">+ ثبت رکورد جدید</button>
                
                <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)} style={customModalStyles} contentLabel="ثبت رکورد جدید">
                    <h2>ثبت اطلاعات جدید</h2>
                    <form onSubmit={handleFormSubmit} className="growth-form-modal">
                        <div className="form-group"><label>تاریخ</label><DatePicker selected={newRecord.date} onChange={handleDateChange} dateFormat="yyyy/MM/dd" /></div>
                        <div className="form-group"><label>قد (سانتی‌متر)</label><input type="number" name="height" value={newRecord.height} onChange={handleInputChange} required /></div>
                        <div className="form-group"><label>وزن (کیلوگرم)</label><input type="number" step="0.1" name="weight" value={newRecord.weight} onChange={handleInputChange} required /></div>
                        <div className="modal-actions"><button type="submit" className="btn-save">ثبت</button><button type="button" onClick={() => setIsModalOpen(false)} className="btn-cancel">انصراف</button></div>
                    </form>
                </Modal>

                <div className="summary-cards">{/* ... */}</div>
                <div className="data-table-card">{/* ... collapsible table ... */}</div>
                <div className="chart-card">{/* ... height chart ... */}</div>
                <div className="chart-card">{/* ... weight chart ... */}</div>
            </div>
        </div>
    );
};

const customModalStyles = {
    content: { top: '50%', left: '50%', right: 'auto', bottom: 'auto', marginRight: '-50%', transform: 'translate(-50%, -50%)', borderRadius: '12px', padding: '2rem', width: '90%', maxWidth: '500px' },
    overlay: { backgroundColor: 'rgba(0, 0, 0, 0.75)' }
};

export default GrowthChartPage;
