import React, { useState, useEffect, useCallback } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import DatePicker from "react-datepicker";
import { format, differenceInMonths, parseISO } from 'date-fns';
import Modal from 'react-modal';
import "react-datepicker/dist/react-datepicker.css";
import './GrowthChartPage.css';
import { heightForAgeBoys, weightForAgeBoys } from '../who-stats';

Modal.setAppElement('#root');

const mergeData = (childBirthDate, growthData, whoData, dataKey) => {
    if (!childBirthDate) return [];
    const combined = whoData.map(who => ({ ...who }));
    growthData.forEach(record => {
        try {
            const recordDate = parseISO(record.date.replace(/\//g, '-'));
            const birthDate = parseISO(childBirthDate.replace(/\//g, '-'));
            const ageInMonths = differenceInMonths(recordDate, birthDate);
            const existing = combined.find(c => c.month === ageInMonths);
            if (existing) { existing[dataKey] = record[dataKey]; }
            else { combined.push({ month: ageInMonths, [dataKey]: record[dataKey] }); }
        } catch (e) { console.error("Date parsing error", e); }
    });
    return combined.sort((a, b) => a.month - b.month);
};

const GrowthChartPage = () => {
    const history = useHistory();
    const { childId } = useParams();
    const [child, setChild] = useState(null);
    const [growthData, setGrowthData] = useState([]);
    const [newRecord, setNewRecord] = useState({ date: new Date(), height: '', weight: '' });
    const [isModalOpen, setIsModalOpen] = useState(false);

    const fetchAllData = useCallback(async () => {
        try {
            const childRes = await fetch(`http://localhost:5000/api/children/${childId}`);
            if (!childRes.ok) throw new Error('Child not found');
            const childData = await childRes.json();
            setChild(childData);

            const growthRes = await fetch(`http://localhost:5000/api/growth/${childId}`);
            const growthRecords = await growthRes.json();
            setGrowthData(growthRecords);
        } catch (error) { history.push('/my-children'); }
    }, [childId, history]);

    useEffect(() => { fetchAllData(); }, [fetchAllData]);

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        const formattedRecord = { ...newRecord, date: format(newRecord.date, 'yyyy/MM/dd') };
        try {
            await fetch(`http://localhost:5000/api/growth/${childId}`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(formattedRecord) });
            fetchAllData();
            setIsModalOpen(false);
        } catch (error) { alert(error.message); }
    };

    if (!child) return <p>در حال بارگذاری...</p>;
    
    const mergedHeightData = mergeData(child.birthDate, growthData, heightForAgeBoys, 'height');
    const mergedWeightData = mergeData(child.birthDate, growthData, weightForAgeBoys, 'weight');

    return (
        <div className="growth-chart-page">
            <nav className="page-nav-final"><button onClick={() => history.push('/my-children')}>&larr; بازگشت</button><h1>پرونده رشد: {child.name}</h1></nav>
            <div className="growth-content-container">
                <button onClick={() => setIsModalOpen(true)} className="add-record-btn">+ ثبت رکورد جدید</button>
                <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)} className="modal" overlayClassName="overlay">
                    <h2>ثبت اطلاعات جدید</h2>
                    <form onSubmit={handleFormSubmit} className="growth-form-modal">
                        <div className="form-group"><label>تاریخ</label><DatePicker selected={newRecord.date} onChange={(date) => setNewRecord(p => ({...p, date}))} dateFormat="yyyy/MM/dd" /></div>
                        <div className="form-group"><label>قد (cm)</label><input type="number" name="height" value={newRecord.height} onChange={(e) => setNewRecord(p => ({...p, height: e.target.value}))} required /></div>
                        <div className="form-group"><label>وزن (kg)</label><input type="number" step="0.1" name="weight" value={newRecord.weight} onChange={(e) => setNewRecord(p => ({...p, weight: e.target.value}))} required /></div>
                        <div className="modal-actions"><button type="submit">ثبت</button><button type="button" onClick={() => setIsModalOpen(false)}>انصراف</button></div>
                    </form>
                </Modal>
                <div className="chart-card"><h2>نمودار قد به سن</h2><ResponsiveContainer width="100%" height={400}><LineChart data={mergedHeightData}><CartesianGrid /><XAxis dataKey="month" /><YAxis /><Tooltip /><Legend /><Line dataKey="height" name="قد کودک" stroke="#8884d8" /><Line dataKey="P50" name="میانه جهانی" stroke="#4caf50" /><Line dataKey="P3" name="صدک ۳" stroke="#ff7300" dot={false}/><Line dataKey="P97" name="صدک ۹۷" stroke="#ff7300" dot={false}/></LineChart></ResponsiveContainer></div>
                <div className="chart-card"><h2>نمودار وزن به سن</h2><ResponsiveContainer width="100%" height={400}><LineChart data={mergedWeightData}><CartesianGrid /><XAxis dataKey="month" /><YAxis /><Tooltip /><Legend /><Line dataKey="weight" name="وزن کودک" stroke="#82ca9d" /><Line dataKey="P50" name="میانه جهانی" stroke="#4caf50" /><Line dataKey="P3" name="صدک ۳" stroke="#ff7300" dot={false}/><Line dataKey="P97" name="صدک ۹۷" stroke="#ff7300" dot={false}/></LineChart></ResponsiveContainer></div>
            </div>
        </div>
    );
};

export default GrowthChartPage;
